{% extends "base.html" %}
{% block title %}Book Room{% endblock %}
{% block content %}
    <div class="container">
        <div class="row justify-content-center">
            <div class="col-md-8">
                <div class="card">
                    <div class="card-header">
                        <h3 class="text-center">Book Room {{ room.room_number }}</h3>
                    </div>
                    <div class="card-body">
                        <div class="room-details mb-4">
                            <h5>Room Details:</h5>
                            <p>Type: {{ room.room_type }}<br>
                               Price per night: ${{ room.price_per_night }}<br>
                               Capacity: {{ room.capacity }} persons</p>
                        </div>
                        <form method="POST" action="{{ url_for('routes.book_room', room_id=room.id) }}">
                            {{ form.hidden_tag() }}
                            <div class="mb-3">
                                {{ form.start_date.label(class="form-label") }}
                                {{ form.start_date(class="form-control") }}
                                {% for error in form.start_date.errors %}
                                    <span class="text-danger">{{ error }}</span>
                                {% endfor %}
                            </div>
                            <div class="mb-3">
                                {{ form.end_date.label(class="form-label") }}
                                {{ form.end_date(class="form-control") }}
                                {% for error in form.end_date.errors %}
                                    <span class="text-danger">{{ error }}</span>
                                {% endfor %}
                            </div>
                            <div class="d-grid gap-2">
                                {{ form.submit(class="btn btn-primary btn-block") }}
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
{% endblock %}
